import java.util.*;

public class Main {

    private static int[] a;

    private static final int MAX = 1010;

    public static void main(String[] args) throws IOException {
        int n = Integer.parseInt(br.readLine());

        // nums ç”¨äºç»Ÿè®¡äººæ•°
        int[] nums = new int[MAX];

        // åˆå§‹åŒ–å¹¶æŸ¥é›†
        a = new int[MAX];
        for (int i = 1; i &lt; a.length; i++) {
            a[i] = i;
        }

        Set&lt;Integer&gt; set = new HashSet&lt;&gt;();
        for (int i = 1; i &lt;= n; i++) {
            String[] s = br.readLine().split(" ");
            int first = Integer.parseInt(s[1]);

            // å¢åŠ äººæ•°
            nums[find(first)]++;

            set.add(first);
            for (int j = 2; j &lt; s.length; j++) {
                int h = Integer.parseInt(s[j]);
                set.add(h);
                // å°†åé¢çš„å…¨éƒ¨å¹¶å…¥ç¬¬ä¸€ä¸ª
                merge(h, first);
            }
        }

        // ç»Ÿè®¡é›†ç¾¤äººæ•°
        Map&lt;Integer, Integer&gt; map = new HashMap&lt;&gt;();
        for (int i = 1; i &lt; a.length; i++) {
            if (set.contains(i)) {
                int ii = find(i);
                if (ii == i) {
                    if (map.containsKey(i)) {
                        map.put(i, map.get(i) + nums[i]);
                    } else {
                        map.put(i, nums[i]);
                    }
                } else {
                    if (map.containsKey(ii)) {
                        map.put(ii, map.get(ii) + nums[i]);
                    } else {
                        map.put(ii, nums[i]);
                    }
                }
            }
        }
        // out
        pw.println(map.size());
        List&lt;Integer&gt; ans = new ArrayList&lt;&gt;(map.values());
        // é€†åºæ’åº
        ans.sort(Collections.reverseOrder());
        int i;
        for (i = 0; i &lt; ans.size() - 1; i++) {
            pw.print(ans.get(i) + " ");
        }
        pw.print(ans.get(i));
        pw.flush();
    }

    private static void merge(int x, int y) {
        x = find(x);
        y = find(y);
        if (x != y) {
            a[x] = y;
        }
    }

    private static int find(int x) {
        if (x != a[x]) {
            a[x] = find(a[x]);
        }
        return a[x];
    }

    private static BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

    private static PrintWriter pw = new PrintWriter(new OutputStreamWriter(System.out));

}

