    public HuffmanNode(int height) {
        this.height = height;
    }

    public HuffmanNode(Byte data, int height) {
        this.data = data;
        this.height = height;
    }

    @Override
    public String toString() {
        return "HuffmanNode{" +
                "data=" + data +
                ", height=" + height +
                ", left=" + left +
                ", right=" + right +
                '}';
    }
}





